import React, { useState } from 'react';

function App() {
  const [result, setResult] = useState(0);

  function increment() {
    setResult(function (prevResult) {
      return prevResult + 1;
    });
  }

  function decrement() {
    setResult(function (prevResult) {
      return prevResult - 1;
    });
  }

  return (
    <div className="flex justify-center items-center flex-col">
      <button
        onClick={increment}
        className="mt-9 rounded-md bg-green-600 border-2 border-white text-white w-16 h-16 text-lg">
        +
      </button>
      <button
        onClick={decrement}
        className="mt-5 rounded-md bg-green-600 border-2 border-white text-white w-16 h-16 text-lg">
        -
      </button>
      <p>{result}</p>
    </div>
  );
}

export default App;
