import React, { useState } from 'react';

function App() {
  const [result, setResult] = useState('I wanna be stronger');

  function changeState() {
    setResult('I wanna be smarter');
  }

  return (
    <div className="flex justify-center items-center flex-col">
      <button
        onClick={changeState}
        className="mt-9 rounded-md bg-green-600 border-2 border-white text-white w-32 h-16">
        Add item
      </button>
      <p>{result}</p>
    </div>
  );
}

export default App;
