import React, { useState } from 'react';

function App() {
  var myArr = [1, 2, 3, 4, 5];

  var paragrapgh = myArr.map(function (item) {
    return <p>{item}</p>;
  });

  var result = useState(myArr, addItem);

  function addItem() {
    myArr.push(myArr.length + 1);
    console.log(myArr);
  }

  return (
    <div className="flex justify-center items-center flex-col">
      <button
        onClick={addItem}
        className="mt-9 rounded-md bg-green-600 border-2 border-white text-white w-32 h-16">
        Add item
      </button>
      {paragrapgh}
      <p>{result}</p>
    </div>
  );
}

export default App;
